import React from 'react';
import logo from './logo.svg';
import './App.css';
const Ipfs = require('ipfs');
const OrbitDB = require('orbit-db');

/*
try {
  // Ipfs and OrbitDB wouldn't be global.
} catch(e) {
  console.log("Error: ", e);
}
*/

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

// This will create an IPFS node and an OrbitDB database when new instance is created
class NewPiecePlease {
  constructor(IPFS, OrbitDB) {
    this.OrbitDB = OrbitDB;
    this.example = 0;

    (async () => {
      console.log("IPFS object: ", IPFS)
      this.node = await IPFS.create();

      //this.node.on("error", (e) => { throw (e) })
      //this.node.on("ready", this._init.bind(this))
      //this._init.bind(this);
      this._init();
    })();
  }

  async _init() {
    this.orbitdb = await this.OrbitDB.createInstance(this.node);
    this.defaultOptions = { accessController: { write: [this.orbitdb.identity.publicKey] }}
    const docStoreOptions = {
      ...this.defaultOptions,
      indexBy: 'hash',
    }
    this.pieces = await this.orbitdb.docstore('pieces', docStoreOptions);
    console.log("ID", this.pieces.id);
    //this.onready();
    this.example = 3;
  }
}

// This is exporting the class
try {
  module.exports = exports = new NewPiecePlease(Ipfs, OrbitDB);
} catch (e) {
  window.NPP = new NewPiecePlease(window.IPFS, window.OrbitDB);
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      NPP: undefined,
      DB_ID: null,
    }
  }
  componentDidMount() {
    // Creating database instance
    this.setState({
      NPP: new NewPiecePlease(Ipfs, OrbitDB),
    }, () => {
      //this.state.NPP._init();
      this.state.NPP = new NewPiecePlease(Ipfs, OrbitDB);
      console.log("Database instance:", this.state.NPP);
      console.log("OrbitDB: ", this.state.NPP.OrbitDB);
      // !!!! The _init function is not assigning variables in the correct namespace!
      console.log("orbitdb: ", this.state.NPP.orbitdb);
      console.log("example: ", this.state.NPP.example);
      //console.log("Table ID: ", this.state.NPP.pieces.id);
    });


  }

  componentDidUpdate() {
    /*this.setState((state, props) => {
      return {
        DB_ID: this.state.NPP.pieces.id,
      }
    });*/
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            This is an OrbitDB test.
          </p>
          <p>{this.state.DB_ID}</p>
        </header>
      </div>
    );
  }
}

export default App;
;
